parser.downloadURL:
(function (options) {
  if (options.url && options.url.href) {
    if (!/(\/t\/(\d+)|\/dl\/(\d+)\/(\d+))/.test(options.url.href)) {
      options.error = {
        success: false,
        msg: "无效的下载地址"
      }
      return;
    }
    // 匹配直接下载地址
    if (/\/dl\/(\d+)\/(\d+)/.test(options.url.href)) {
      options.result = options.url.href;
      return;
    }

    // 匹配种子详细页面地址
    let id_match = options.url.href.match(/\/t\/(\d+)/);
    let passkey = options.site.passkey;
    if (passkey) {
      let id = id_match[1];
      // 格式：vvvid|||passkeyzz
      let key = (new Base64).encode("vvv" + id + "|||" + options.site.passkey + "zz");
      options.result = `https://${options.site.host}/rssdd.php?par=${key}&ssl=yes`;
    } else {
      options.error = {
        success: false,
        msg: "请先设置站点的passkey"
      }
    }
  } else {
    options.error = {
      success: false,
      msg: "无效的下载地址"
    }
  }
})(options)

(function (options) {
  if (options.url && options.url.query && options.url.href.getQueryString) {
    let url = options.url.href;
    let passkey = options.site.passkey || options.url.href.getQueryString("passkey");
    if (url.indexOf("download.php") == -1) {
      let id = url.getQueryString("id");
      if (id) {
        // 如果站点没有配置禁用https，则默认添加https链接
        url = options.url.origin + "/download.php?id=" + id + (passkey ? "&passkey=" + passkey : "") + (options.site.disableHttps ? "" : "&https=1");
      } else {
        url = "";
      }
    }

    if (!url) {
      options.error = {
        success: false,
        msg: "无效的下载地址"
      }
      return;
    }

    options.result = url;
  }
})(options)
